                and cid >= (SELECT FLOOR( MAX(cid) * RAND()) FROM `${prefix}_contents` )
            
        
        
            order by ${order} desc
        
        limit ${page}, ${pageSize}

        SELECT count(*) FROM ${prefix}_contents
        
            
                and `cid` = #{cid}
            
            
